-- ===== Overview =====
-- App name: Quantum Computing Simulation
-- Description: PostgreSQL DDL - tables and reset function

-- ===== Reset Function =====
CREATE OR REPLACE FUNCTION sp_load_qcdb()
RETURNS TABLE(code INTEGER, message VARCHAR) AS $$
BEGIN
    -- Drop tables if exist (order matters for foreign keys)
    DROP TABLE IF EXISTS "Shots" CASCADE;
    DROP TABLE IF EXISTS "Simulations" CASCADE;
    DROP TABLE IF EXISTS "Gates" CASCADE;
    DROP TABLE IF EXISTS "States" CASCADE;

    -- ===== Create Tables =====
    CREATE TABLE "States" (
        "stateID"     SERIAL PRIMARY KEY,
        "name"        VARCHAR(45) NOT NULL UNIQUE,
        "alphaReal"   NUMERIC(9,8) NOT NULL,
        "alphaImgn"   NUMERIC(9,8) NOT NULL,
        "betaReal"    NUMERIC(9,8) NOT NULL,
        "betaImgn"    NUMERIC(9,8) NOT NULL,
        "symbol"      VARCHAR(45) NOT NULL UNIQUE,
        "description" VARCHAR(100) NOT NULL
    );

    CREATE TABLE "Gates" (
        "gateID"      SERIAL PRIMARY KEY,
        "symbol"      VARCHAR(45) NOT NULL UNIQUE,
        "name"        VARCHAR(45) NOT NULL UNIQUE,
        "description" VARCHAR(100) NOT NULL
    );

    CREATE TABLE "Simulations" (
        "simID"   SERIAL PRIMARY KEY,
        "stateID" INTEGER NOT NULL REFERENCES "States"("stateID") ON DELETE CASCADE ON UPDATE CASCADE,
        "gateID"  INTEGER NOT NULL REFERENCES "Gates"("gateID") ON DELETE CASCADE ON UPDATE CASCADE
    );

    CREATE INDEX idx_sim_state ON "Simulations"("stateID");
    CREATE INDEX idx_sim_gate ON "Simulations"("gateID");

    CREATE TABLE "Shots" (
        "shotID"      SERIAL PRIMARY KEY,
        "simID"       INTEGER NOT NULL REFERENCES "Simulations"("simID") ON DELETE CASCADE ON UPDATE CASCADE,
        "alphaReal"   NUMERIC(9,8) NOT NULL,
        "alphaImgn"   NUMERIC(9,8) NOT NULL,
        "betaReal"    NUMERIC(9,8) NOT NULL,
        "betaImgn"    NUMERIC(9,8) NOT NULL,
        "outputState" SMALLINT NOT NULL
    );

    CREATE INDEX idx_shots_sim ON "Shots"("simID");

    -- ===== Sample Data =====
    -- States (IDs 1-6 are standard quantum states, protected from edit/delete)

    -- Reset sequence to continue after manual IDs
    PERFORM setval('"States_stateID_seq"', 7);

    -- Gates

    PERFORM setval('"Gates_gateID_seq"', 5);

    -- Simulations

    PERFORM setval('"Simulations_simID_seq"', 2);

    -- Shots for Simulation 1 (|0> + H gate) - 100 shots

    -- Shots for Simulation 2 (|1> + I gate) - 100 shots

    -- Reset shot sequence
    PERFORM setval('"Shots_shotID_seq"', 2099);

    RETURN QUERY SELECT 200, 'Reset successfully'::VARCHAR;
END;
$$ LANGUAGE plpgsql;
